import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './Home';
import Statistics from './Statistics';
import './index.css';

function App() {
  const [stats, setStats] = useState(null);
  const [filter, setFilter] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/statistics')
      .then((response) => response.json())
      .then((data) => setStats(data));
  }, []);

  const handleBarClick = (category, value) => {
    setFilter({ category, value });
  };

  return (
    <Router>
      <div className="app">
        <h1>Image Dataset App</h1>
        <nav>
          <Link to="/">Home</Link> | <Link to="/statistics">Statistics</Link>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/statistics"
            element={<Statistics stats={stats} filter={filter} onBarClick={handleBarClick} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
