import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function BarChart({ data, title, onBarClick, filter, category, stats }) {
  const chartRef = useRef();

  useEffect(() => {
    if (!data || !chartRef.current) return;

    // Clear the existing chart
    d3.select(chartRef.current).selectAll('*').remove();

    // Chart dimensions
    const width = 300;
    const height = 200;
    const margin = { top: 30, right: 30, bottom: 50, left: 40 };

    const svg = d3
      .select(chartRef.current)
      .append('svg')
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // X-axis
    const x = d3
      .scaleBand()
      .domain(data.map(([key]) => key))
      .range([0, width])
      .padding(0.2);

    svg
      .append('g')
      .attr('transform', `translate(0,${height})`)
      .call(d3.axisBottom(x));

    // Y-axis
    const y = d3
      .scaleLinear()
      .domain([0, d3.max(data, ([, value]) => value)])
      .nice()
      .range([height, 0]);

    svg.append('g').call(d3.axisLeft(y));

    // Bars
    svg
      .selectAll('rect')
      .data(data)
      .enter()
      .append('rect')
      .attr('x', ([key]) => x(key))
      .attr('y', ([, value]) => y(value))
      .attr('width', x.bandwidth())
      .attr('height', ([, value]) => height - y(value))
      .attr('fill', ([key]) =>
        filter && filter.category === category && filter.value === key ? 'orange' : 'steelblue'
      )
      .on('click', ([key]) => onBarClick(category, key));

    // Labels
    svg
      .selectAll('text.label')
      .data(data)
      .enter()
      .append('text')
      .attr('class', 'label')
      .attr('x', ([key]) => x(key) + x.bandwidth() / 2)
      .attr('y', ([, value]) => y(value) - 5)
      .attr('text-anchor', 'middle')
      .text(([, value]) => value);

  }, [data, filter, onBarClick, category, stats]);

  return (
    <div className="chart">
      <h2>{title}</h2>
      <div ref={chartRef}></div>
    </div>
  );
}

export default BarChart;
