import React from 'react';
import './BarChart.css';

function BarChart({ data, title, onBarClick, filter, category }) {
  return (
    <div className="chart">
      <h3>{title}</h3>
      <svg width={300} height={200}>
        {data.map(([key, value], index) => (
          <g
            key={key}
            transform={`translate(${index * 80 + 20}, 0)`}
            onClick={() => onBarClick(category, key)} // Pass category and value to parent
            style={{ cursor: 'pointer' }}
          >
            <rect
              x={0}
              y={200 - value * 10}
              width={50}
              height={value * 10}
              fill={filter?.category === category && filter?.value === key ? 'orange' : 'steelblue'} // Highlight selected bar
            />
            <text
              x={25}
              y={190 - value * 10}
              textAnchor="middle"
              fontSize="12"
              fill="black"
            >
              {value}
            </text>
            <text
              x={25}
              y={210}
              textAnchor="middle"
              fontSize="12"
              fill="black"
            >
              {key}
            </text>
          </g>
        ))}
      </svg>
    </div>
  );
}

export default BarChart;
