import React, { useState } from "react";
import BarChart from "./components/BarChart";
import FilteredImages from "./components/FilteredImages";
import stats from "./data/stats.json";

const Statistics = () => {
  const [filteredImages, setFilteredImages] = useState(stats.images);

  const handleBarClick = (category) => {
    let filteredIds = [];
    if (category === "male") {
      filteredIds = stats.gender.male_ids;
    } else if (category === "female") {
      filteredIds = stats.gender.female_ids;
    }
    const newFilteredImages = stats.images.filter(image => filteredIds.includes(image.id));
    setFilteredImages(newFilteredImages);
  };

  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-around" }}>
        <BarChart
          title="Gender Distribution"
          data={[
            { label: "male", value: stats.gender.male },
            { label: "female", value: stats.gender.female }
          ]}
          onBarClick={handleBarClick}
        />
      </div>
      <FilteredImages images={newFilteredImages} />
    </div>
  );
};

export default Statistics;
