import React, { useState, useEffect } from 'react';
import BarChart from './BarChart';
import './index.css';

function Statistics() {
  const [stats, setStats] = useState(null);
  const [filter, setFilter] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/statistics')
      .then((response) => response.json())
      .then((data) => setStats(data));
  }, []);

  const handleBarClick = (category, value) => {
    setFilter({ category, value });
  };

  return (
    <div>
      <h2>Statistics - Bar Charts</h2>
      {stats ? (
        <div className="chart-container">
          <BarChart
            data={Object.entries(stats.gender).filter(([key]) => !key.endsWith('_ids'))}
            title="Gender Distribution"
            onBarClick={handleBarClick}
            filter={filter}
          />
          <BarChart
            data={Object.entries(stats.age).filter(([key]) => !key.endsWith('_ids'))}
            title="Age Distribution"
            onBarClick={handleBarClick}
            filter={filter}
          />
          <BarChart
            data={Object.entries(stats.orientation).filter(([key]) => !key.endsWith('_ids'))}
            title="Orientation Distribution"
            onBarClick={handleBarClick}
            filter={filter}
          />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default Statistics;
